package com.adanedhel.hafta02.gun04;

public class WhileTime05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		int toplam = 0;
		while (i < 100) {
			toplam += i;
			
			i++;
			
		}
		System.out.println("1'den 100 arasi toplamlari: "+ toplam);

	}

}
